func (bls *BasicLinkSelect) sqlScan(ctx context.Context, v interface{}) error {
	rows := &sql.Rows{}
	query, args := bls.sqlQuery().Query()
	if err := bls.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (bls *BasicLinkSelect) sqlQuery() sql.Querier {
	selector := bls.sql
	selector.Select(selector.Columns(bls.fields...)...)
	return selector
}
